mapper.delete:
delete from sgnc_currency where dr=0
and id in (#{idstr})
and id = #{id}

mapper.insert:

    SELECT LAST_INSERT_ID()

insert into sgnc_currency (code, name, symbol, cur_precision,
creator, creationtime, dr, ts)
values (#{code,jdbcType=VARCHAR}, #{name,jdbcType=VARCHAR}, #{symbol,jdbcType=VARCHAR}, #{precision,jdbcType=VARCHAR},
#{creator,jdbcType=BIGINT}, #{creationtime,jdbcType=CHAR}, #{dr,jdbcType=INTEGER}, #{ts,jdbcType=CHAR}
)

insert into sgnc_currency (code, name, symbol, cur_precision,
creator, creationtime, dr, ts
) values

    (#{item.code,jdbcType=VARCHAR}, #{item.name,jdbcType=VARCHAR}, #{item.symbol,jdbcType=VARCHAR}, #{item.precision,jdbcType=VARCHAR},
    #{item.creator,jdbcType=BIGINT}, #{item.creationtime,jdbcType=CHAR},
    #{item.dr,jdbcType=INTEGER}, #{item.ts,jdbcType=CHAR}
    )

update.foreach:
update sgnc_currency
code = #{item.code,jdbcType=VARCHAR}, name = #{item.name,jdbcType=VARCHAR}, symbol = #{item.symbol,jdbcType=VARCHAR}, cur_precision = #{item.precision,jdbcType=VARCHAR}, modifier = #{item.modifier,jdbcType=BIGINT}, modifytime = #{item.modifytime,jdbcType=CHAR}, ts = #{item.ts,jdbcType=CHAR}
where id = #{item.id,jdbcType=BIGINT}
